(function($){
	"use strict";
	$('#jscript').hide();
	$('#body').show();
	$(document).ready(function(){
		if (!window.WebSocket) alert("WebSocket not supported by this browser");
		$('#scanmsg').hide();
		$('#checkout').hide();
		$('#checkin').hide();

		/**
		 * WebSocket for App
		 */
		var CommitCode = {
			websocket: null,
			init: function() {
				var self = this;
				var address = wsconfig['address'];
				address = address ? address : window.location.hostname;
				var port = wsconfig['port'];
				port = port ? ':' + port : '';
				/*var uri = (document.location.protocol === "http:" ? "ws:" : "wss:") + "//" + address + port + '/ws';*/
				var uri = (document.location.protocol === "http:" ? "ws:" : "wss:") + "//" + address + port;
				self.websocket = new WebSocket(uri);
				self.websocket.onopen = function(e) { /*console.log('onopen', e);*/ };
				self.websocket.onclose = function(e) { self.show_message('valid','invalid', 'Hm. Irgendwie ist die Verbindung zusammengebrochen. Lad die Seite nochmal!', false);};
				self.websocket.onmessage = function(e) { self.onmessage(e); };
				self.websocket.onerror = function(e) { console.log('onerror', e); };
				return self;
			},
			onmessage: function(e) {
				var self = this;
				//console.log('data incoming');
				try {
					var data = JSON.parse(e.data);
					
					//console.log(data);
					//console.log(data);
					// Handling websocket messaging 
					if('exists' in data) {
						if (data['exists'] == true && data['known'] == true) {
							$("#scanmsg").hide();
							CommitCode.show_message('invalid','valid', 'Bereit für Checkout', true);
							$('#checkout').show();
							$('#checkin').hide();
							if (automode == 1) {
								CommitCode.websocket.send(
										JSON.stringify(
												{
												'checkout': $('#checkout').val(),
												'devision': loc_id,
												}
										)
									);
								}
						}
						if (data['exists'] == true && data['known'] == false) {
							$("#scanmsg").hide();
							CommitCode.show_message('invalid','valid', 'Bereit für Checkin', true);
							$('#checkout').hide();
							$('#checkin').show();
							CommitCode.websocket.send(
									JSON.stringify(
											{
											'checkin': $('#checkin').val(),
											'devision': loc_id,
											}
									)
								);
						}
						if (data['exists'] == false) {
							$('#checkout').hide();
							$('#checkin').hide();
							CommitCode.show_message('valid','invalid', 'Ungültiger oder unbekannter QR-Code', true);
						}
					}
					if( ('cooldown' in data) && data['cooldown'] == 1) {
						CommitCode.show_message('valid','invalid', 'Cooldown aktiv', true);
						$('#checkout').hide();
						$('#checkin').hide()
					}
					if('checkin' in data) {
						if (data['checkin'] == true) {
							CommitCode.show_message('invalid','valid', data['msg'], true);
							$('#checkin').hide();
							var snd = new Audio("data:audio/wav;base64,");  
							snd.play();
						}
						else {
							CommitCode.show_message('valid','invalid', data['msg'], true);
						}
					}
					if('checkout' in data) {
						if (data['checkout'] == true) {
							CommitCode.show_message('invalid','valid', data['msg'], true);
							$('#checkout').hide();
							var snd = new Audio("data:audio/wav;base64,");  
							snd.play();
						}
						else {
							CommitCode.show_message('valid','invalid', data['msg'], true);
						}
					}
					if('COUNTER' in data && data['DEVISION'] == loc_id) {
						$("#check_count").text(data['COUNTER']);
					}
				} catch(e) {
					//console.log('onmessage', e);
				}
			},
			success: function() {
				CommitCode.show_message('invalid','valid', 'Operation Erfolgreich', true);
			},
			fail: function() {
				CommitCode.show_message('valid','invalid', 'Fehler!!!!', false);
			},
			known: function() {
				CommitCode.show_message('valid','invalid', 'Ungültiger oder unbekannter QR-Code', true);
			},
			show_message: function(rem, add, txt, hide=true) {
				$("#scanmsg").hide();
				$("#scanmsg").removeClass(rem);
				$("#scanmsg").addClass(add);
				$("#scanmsg").text(txt);
				if(hide) {
					//console.log('willhide');
					$("#scanmsg").show().delay(5000).fadeOut();
				} else {
					//console.log('willfade');
					$("#scanmsg").show();
				};
			},
		}.init();

		var app = new Vue({
			el: '#app',
			data: {
				scanner: null,
				activeCameraId: null,
				cameras: [],
				scans: []
			},
			mounted: function () {
				var self = this;
				let opts = {
						  // Whether to scan continuously for QR codes. If false, use scanner.scan() to manually scan.
						  // If true, the scanner emits the "scan" event when a QR code is scanned. Default true.
						  continuous: true,
						  
						  // The HTML element to use for the camera's video preview. Must be a <video> element.
						  // When the camera is active, this element will have the "active" CSS class, otherwise,
						  // it will have the "inactive" class. By default, an invisible element will be created to
						  // host the video.
						  video: document.getElementById('preview'),
						  
						  // Whether to horizontally mirror the video preview. This is helpful when trying to
						  // scan a QR code with a user-facing camera. Default true.
						  mirror: false,
						  
						  // Whether to include the scanned image data as part of the scan result. See the "scan" event
						  // for image format details. Default false.
						  captureImage: false,
						  
						  // Only applies to continuous mode. Whether to actively scan when the tab is not active.
						  // When false, this reduces CPU usage when the tab is not active. Default true.
						  backgroundScan: false,
						  
						  // Only applies to continuous mode. The period, in milliseconds, before the same QR code
						  // will be recognized in succession. Default 5000 (5 seconds).
						  //refractoryPeriod: 10000,
						  refractoryPeriod: 5000,
						  
						  // Only applies to continuous mode. The period, in rendered frames, between scans. A lower scan period
						  // increases CPU usage but makes scan response faster. Default 1 (i.e. analyze every frame).
						  scanPeriod: 1
						};
				/*self.scanner = new Instascan.Scanner({ video: document.getElementById('preview'), scanPeriod: 5, mirror: true });*/
				self.scanner = new Instascan.Scanner(opts);
				self.scanner.addListener('scan', function (content, image) {
					self.scans = [{ date: +(Date.now()), content: content }];
					//console.log(content);
					$('#checkin').val(content);
					$('#checkout').val(content);
					CommitCode.websocket.send(
						JSON.stringify(
								{
								'exists': content,
								'devision': loc_id,
								}
						)
					);
					fitvideo();
				});
				Instascan.Camera.getCameras().then(function (cameras) {
				self.cameras = cameras;
					if (cameras.length > 0) {
						cameras.reverse();
						self.activeCameraId = cameras[0].id;
						self.scanner.start(cameras[0]);
						/*setTimeout(fitvideo, 3000);*/
					} else {
					console.error('No cameras found.');
					}
				}).catch(function (e) {
					console.error(e);
				});
			},
			methods: {
				formatName: function (name) {
					return name || '(unknown)';
				},
				selectCamera: function (camera) {
					this.activeCameraId = camera.id;
					this.scanner.start(camera);
					/*setTimeout(fitvideo, 3000);*/
				}
			}
		});
		//$('#preview').click( fitvideo() );
		$('#preview').on('canplay', fitvideo() );
		$('#checkin').click( function( e ) {
			CommitCode.websocket.send(
					JSON.stringify(
							{
							'checkin': this.value,
							'devision': loc_id,
							}
					)
				);
		});
		$("#checkout").click( function( e ) {
			CommitCode.websocket.send(
					JSON.stringify(
							{
							'checkout': this.value,
							'devision': loc_id,
							}
					)
				);
		});
		$("#resizecam").click( function( e ) {
			/*$('.cameras').toggleClass('hide');*/
			console.log('resize');
			fitvideo();
		});
		function fitvideo() {
			var winheight = $(window).height();
			var position = $('#preview').position().top + 5;
			$('#preview').css({'max-height' : winheight - position});
		};
		$("#hamburgicon").click( function () {
			$("#scanmenu").toggleClass("checked");
			console.log('checking');
		});
	});
})(jQuery);